    const expect = 200;
    const real = getMyWeather(city, false);
    if (!expect===real) {
      throw new Error(`Expected ${expect}, but got ${real}:`);
    } else {
      console.log("OK:");
    }
    });

  it ('check when city does not exist', () => {
    const city = 'hhh';
    const expect = 404;
    const real = getMyWeather(city, false);
    if (!expect===real){
      throw new Error('Expected ' + expect + ', but got ' + real + ":");
    } else {
      console.log("OK:");
    }
  });
});

